    "urdu": "اور تمہارے پہلے باپ دادا؟",
    "arabic": "اَنۡتُمۡ  وَ  اٰبَآؤُکُمُ  الۡاَقۡدَمُوۡنَ ﴿۫ ۖ۷۶﴾ ",
    "ayat_id": 3033,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "تو بے شک وہ میرے دشمن ہیں مگر (میں عبادت کرتا ہوں) سارے جہانوں کے رب کی۔",
    "arabic":
        "فَاِنَّہُمۡ عَدُوٌّ  لِّیۡۤ   اِلَّا  رَبَّ  الۡعٰلَمِیۡنَ ﴿ۙ۷۷﴾ ",
    "ayat_id": 3034,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "وہ جس نے مجھے پیدا کیا پس وہی مجھے راہ دکھاتا ہے،",
    "arabic": "الَّذِیۡ خَلَقَنِیۡ فَہُوَ  یَہۡدِیۡنِ ﴿ۙ۷۸﴾ ",
    "ayat_id": 3035,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور وہی جو مجھے کِھلاتا ہے اور وہی مجھے پلاتا ہے۔",
    "arabic": "وَ الَّذِیۡ ہُوَ  یُطۡعِمُنِیۡ وَ  یَسۡقِیۡنِ ﴿ۙ۷۹﴾ ",
    "ayat_id": 3036,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور جب میں بیمار ہوتا ہوں تو وہ مجھے شفا دیتا ہے۔",
    "arabic": "وَ  اِذَا  مَرِضۡتُ  فَہُوَ  یَشۡفِیۡنِ ﴿۪ۙ۸۰﴾ ",
    "ayat_id": 3037,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور وہ جو مجھے موت (سے ہمکنار) کرے گا، پھر مجھے زندہ کرے گا۔",
    "arabic": "وَ الَّذِیۡ  یُمِیۡتُنِیۡ ثُمَّ   یُحۡیِیۡنِ ﴿ۙ۸۱﴾ ",
    "ayat_id": 3038,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "اور وہ جس سے میں امید رکھتا ہوں کہ مجھے بدلہ کے دن میری خطائیں بخش دے گا۔",
    "arabic":
        "وَ الَّذِیۡۤ  اَطۡمَعُ اَنۡ یَّغۡفِرَ لِیۡ خَطِیۡٓئَتِیۡ یَوۡمَ  الدِّیۡنِ ﴿ؕ۸۲﴾ ",
    "ayat_id": 3039,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "اے میرے رب! مجھے حکم و حکمت عطا فرما اور مجھے نیک بندوں کے ساتھ ملا دے۔",
    "arabic": "رَبِّ ہَبۡ لِیۡ حُکۡمًا وَّ اَلۡحِقۡنِیۡ بِالصّٰلِحِیۡنَ ﴿ۙ۸۳﴾ ",
    "ayat_id": 3040,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور میرا ذکرِخیر (جاری) رکھ بعد میں آنے والوں میں۔",
    "arabic": "وَ اجۡعَلۡ لِّیۡ  لِسَانَ صِدۡقٍ فِی الۡاٰخِرِیۡنَ ﴿ۙ۸۴﴾ ",
    "ayat_id": 3041,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور مجھے نعمتوں والی بہشت کے وارثوں میں سے بنا دے۔",
    "arabic": "وَ اجۡعَلۡنِیۡ  مِنۡ وَّرَثَۃِ  جَنَّۃِ النَّعِیۡمِ ﴿ۙ۸۵﴾ ",
    "ayat_id": 3042,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور میرے باپ کو بخش دے، بے شک وہ گمراہوں میں سے ہے۔",
    "arabic": "وَ اغۡفِرۡ  لِاَبِیۡۤ  اِنَّہٗ کَانَ مِنَ الضَّآلِّیۡنَ ﴿ۙ۸۶﴾ ",
    "ayat_id": 3043,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اورتو مجھے اس دن رسوا نہ کرنا جب سب اٹھائے جائیں گے۔",
    "arabic": "وَ لَا  تُخۡزِنِیۡ  یَوۡمَ  یُبۡعَثُوۡنَ ﴿ۙ۸۷﴾ ",
    "ayat_id": 3044,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "جس دن نہ کام آئے گا مال اور نہ بیٹے۔",
    "arabic": "یَوۡمَ لَا  یَنۡفَعُ  مَالٌ  وَّ  لَا  بَنُوۡنَ ﴿ۙ۸۸﴾ ",
    "ayat_id": 3045,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "مگر جو اللہ کے پاس پاک (بے عیب) دل لے کر آیا۔",
    "arabic": "اِلَّا  مَنۡ  اَتَی اللّٰہَ  بِقَلۡبٍ سَلِیۡمٍ ﴿ؕ۸۹﴾ ",
    "ayat_id": 3046,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور جنت پرہیزگاروں کے نزدیک کردی جائے گی،",
    "arabic": "وَ اُزۡلِفَتِ  الۡجَنَّۃُ   لِلۡمُتَّقِیۡنَ ﴿ۙ۹۰﴾ ",
    "ayat_id": 3047,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور دوزخ ظاہر کردی جائے گی گمراہوں کے لیے۔",
    "arabic": "وَ  بُرِّزَتِ الۡجَحِیۡمُ  لِلۡغٰوِیۡنَ ﴿ۙ۹۱﴾ ",
    "ayat_id": 3048,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور انہیں کہا جائے گا کہاں ہیں وہ جن کی تم پرستش کرتے تھے",
    "arabic": "وَ  قِیۡلَ  لَہُمۡ اَیۡنَمَا کُنۡتُمۡ تَعۡبُدُوۡنَ ﴿ۙ۹۲﴾ ",
    "ayat_id": 3049,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "اللہ کے سوا، کیا وہ تمہاری مدد کرسکتے ہیں؟ یا (خود) بدلہ لے سکتے ہیں؟",
    "arabic":
        "مِنۡ دُوۡنِ اللّٰہِ ؕ ہَلۡ یَنۡصُرُوۡنَکُمۡ  اَوۡ یَنۡتَصِرُوۡنَ ﴿ؕ۹۳﴾ ",
    "ayat_id": 3050,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "پس وہ اور گمراہ اس (جہنم) میں اوندھے منہ ڈالے جائیں گے۔",
    "arabic": "فَکُبۡکِبُوۡا فِیۡہَا  ہُمۡ  وَ الۡغَاوٗنَ ﴿ۙ۹۴﴾ ",
    "ayat_id": 3051,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور ابلیس کے لشکر سب کے سب۔",
    "arabic": "وَ  جُنُوۡدُ   اِبۡلِیۡسَ  اَجۡمَعُوۡنَ ﴿ؕ۹۵﴾ ",
    "ayat_id": 3052,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
