    "ayat_id": 3054,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "جب ہم تمہیں سارے جہانوں کے رب کے ساتھ برابر ٹھہراتے تھے۔",
    "arabic": "اِذۡ  نُسَوِّیۡکُمۡ  بِرَبِّ الۡعٰلَمِیۡنَ ﴿۹۸﴾ ",
    "ayat_id": 3055,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
