    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "اور تو صرف ہم جیسا ایک بشر ہے، اور البتہ ہم تجھے جھوٹوں میں سے گمان کرتے ہیں۔",
    "arabic":
        "وَ مَاۤ  اَنۡتَ اِلَّا بَشَرٌ  مِّثۡلُنَا وَ  اِنۡ  نَّظُنُّکَ لَمِنَ  الۡکٰذِبِیۡنَ ﴿۱۸۶﴾ۚ ",
    "ayat_id": 3143,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "سو تو ہم پر آسمان کا ایک ٹکڑا گرا دے اگر تو سچّوں میں سے ہے (سچّا ہے)۔",
    "arabic":
        "فَاَسۡقِطۡ عَلَیۡنَا کِسَفًا مِّنَ السَّمَآءِ  اِنۡ  کُنۡتَ مِنَ  الصّٰدِقِیۡنَ ﴿۱۸۷﴾ؕ ",
    "ayat_id": 3144,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "شعیب (علیہ السلام) نے فرمایا، میرا رب خوب جانتا ہے جو کچھ تم کرتے ہو۔",
    "arabic": "قَالَ  رَبِّیۡۤ   اَعۡلَمُ  بِمَا تَعۡمَلُوۡنَ ﴿۱۸۸﴾ ",
    "ayat_id": 3145,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "تو انہوں نے اسے جھٹلایا، پس انہیں (آگ کے) سائبان والے دن عذاب نے آپکڑا۔ بے شک وہ بڑے سخت دن کا عذاب تھا۔",
    "arabic":
        "فَکَذَّبُوۡہُ  فَاَخَذَہُمۡ عَذَابُ یَوۡمِ  الظُّلَّۃِ ؕ اِنَّہٗ  کَانَ عَذَابَ  یَوۡمٍ  عَظِیۡمٍ ﴿۱۸۹﴾ ",
    "ayat_id": 3146,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "بے شک اس میں نشانی ہے، اور ان کے اکثر ایمان لانے والے نہ تھے۔",
    "arabic":
        "اِنَّ فِیۡ  ذٰلِکَ لَاٰیَۃً ؕ وَ مَا کَانَ  اَکۡثَرُہُمۡ مُّؤۡمِنِیۡنَ ﴿۱۹۰﴾ ",
    "ayat_id": 3147,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور بے شک تیرا رب غالب ہے، نہایت مہربان۔",
    "arabic": "وَ  اِنَّ  رَبَّکَ  لَہُوَ  الۡعَزِیۡزُ  الرَّحِیۡمُ ﴿۱۹۱﴾٪ ",
    "ayat_id": 3148,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور بے شک یہ (قرآن) سارے جہانوں کے رب کا اتارا ہوا ہے۔",
    "arabic": "وَ  اِنَّہٗ   لَتَنۡزِیۡلُ  رَبِّ  الۡعٰلَمِیۡنَ ﴿۱۹۲﴾ؕ ",
    "ayat_id": 3149,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اس کو لے کر اُترا ہے جبریل امینؑ،",
    "arabic": "نَزَلَ  بِہِ  الرُّوۡحُ  الۡاَمِیۡنُ ﴿۱۹۳﴾ۙ ",
    "ayat_id": 3150,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "تمہارے دل پر، تاکہ تم ڈر سنانے والوں میں سے ہو۔",
    "arabic": "عَلٰی قَلۡبِکَ لِتَکُوۡنَ مِنَ الۡمُنۡذِرِیۡنَ ﴿۱۹۴﴾ ",
    "ayat_id": 3151,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "روشن، واضح عربی زبان میں۔",
    "arabic": "بِلِسَانٍ عَرَبِیٍّ مُّبِیۡنٍ ﴿۱۹۵﴾ؕ ",
    "ayat_id": 3152,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور بے شک یہ (اس کا ذکر) پہلے پیغمبروں کے صحیفوں میں ہے۔",
    "arabic": "وَ  اِنَّہٗ  لَفِیۡ  زُبُرِ الۡاَوَّلِیۡنَ ﴿۱۹۶﴾ ",
    "ayat_id": 3153,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "کیا یہ ان کے لیے ایک نشانی نہیں؟ کہ اسے جانتے ہیں علمائے بنی اسرائیل۔",
    "arabic":
        "اَوَ لَمۡ  یَکُنۡ لَّہُمۡ اٰیَۃً  اَنۡ یَّعۡلَمَہٗ عُلَمٰٓؤُا بَنِیۡۤ   اِسۡرَآءِیۡلَ ﴿۱۹۷﴾ؕ ",
    "ayat_id": 3154,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور اگر ہم اسے کسی غیر عربی (زبان دان) پر نازل کرتے،",
    "arabic": "وَ لَوۡ  نَزَّلۡنٰہُ عَلٰی بَعۡضِ الۡاَعۡجَمِیۡنَ ﴿۱۹۸﴾ۙ ",
    "ayat_id": 3155,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu":
        "پھر وہ اسے ان کے سامنے پڑھتا (پھر بھی) وہ اس پر ایمان لانے والے نہ ہوتے (ایمان نہ لاتے)۔",
    "arabic": "فَقَرَاَہٗ  عَلَیۡہِمۡ مَّا کَانُوۡا بِہٖ مُؤۡمِنِیۡنَ ﴿۱۹۹﴾ؕ ",
    "ayat_id": 3156,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اسی طرح ہم نے مجرموں کے دلوں میں انکار داخل کردیا ہے۔",
    "arabic": "کَذٰلِکَ سَلَکۡنٰہُ  فِیۡ قُلُوۡبِ الۡمُجۡرِمِیۡنَ ﴿۲۰۰﴾ؕ ",
    "ayat_id": 3157,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "وہ اس پر ایمان نہ لائیں گے یہاں تک کہ وہ دردناک عذاب دیکھ لیں گے۔",
    "arabic":
        "لَا یُؤۡمِنُوۡنَ بِہٖ حَتّٰی یَرَوُا الۡعَذَابَ الۡاَلِیۡمَ ﴿۲۰۱﴾ۙ ",
    "ayat_id": 3158,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "تو وہ ان پر اچانک آجائے گا اور انہیں خبر بھی نہ ہوگی۔",
    "arabic": "فَیَاۡتِیَہُمۡ  بَغۡتَۃً   وَّ  ہُمۡ  لَا  یَشۡعُرُوۡنَ ﴿۲۰۲﴾ۙ ",
    "ayat_id": 3159,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "پھر وہ کہیں گے کیا ہمیں مہلت دی جائے گی؟",
    "arabic": "فَیَقُوۡلُوۡا ہَلۡ  نَحۡنُ  مُنۡظَرُوۡنَ ﴿۲۰۳﴾ؕ ",
    "ayat_id": 3160,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "پس کیا وہ ہمارے عذاب کو جلدی چاہتے ہیں؟",
    "arabic": "اَفَبِعَذَابِنَا یَسۡتَعۡجِلُوۡنَ ﴿۲۰۴﴾ ",
    "ayat_id": 3161,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "کیا تم نے دیکھا؟ (ذرا دیکھو) اگر ہم انہیں برسوں فائدہ پہنچائیں۔",
    "arabic": "اَفَرَءَیۡتَ اِنۡ مَّتَّعۡنٰہُمۡ سِنِیۡنَ ﴿۲۰۵﴾ۙ ",
    "ayat_id": 3162,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "پھر ان پر پہنچے جس کی انہیں وعید کی جاتی تھی۔",
    "arabic": "ثُمَّ  جَآءَہُمۡ  مَّا  کَانُوۡا  یُوۡعَدُوۡنَ ﴿۲۰۶﴾ۙ ",
    "ayat_id": 3163,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "جس سے وہ فائدہ اٹھاتے تھے ان کے کیا کام آئے گا؟",
    "arabic": "مَاۤ  اَغۡنٰی عَنۡہُمۡ مَّا کَانُوۡا یُمَتَّعُوۡنَ ﴿۲۰۷﴾ؕ ",
    "ayat_id": 3164,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "اور ہم نے کسی بستی کو ہلاک نہیں کیا، مگر اس کے لیے ڈرانے والے",
    "arabic":
        "وَ مَاۤ  اَہۡلَکۡنَا مِنۡ قَرۡیَۃٍ  اِلَّا لَہَا مُنۡذِرُوۡنَ ﴿۲۰۸﴾٭ۖۛ ",
    "ayat_id": 3165,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
    "urdu": "نصیحت کے لیے (پہلے بھیجے) اور ہم ظلم کرنے والے نہ تھے۔",
    "arabic": "ذِکۡرٰی ۟ ۛ وَ مَا کُنَّا ظٰلِمِیۡنَ ﴿۲۰۹﴾ ",
    "ayat_id": 3166,
    "surah_id": 26,
    "surah_name": "Ash-Shu'ara",
    "para_id": 19,
    "para_name": "Wa Qalallazina"
  },
  {
