    "arabic":
        "وَ مَا  کَانَ لَنَا عَلَیۡکُمۡ مِّنۡ سُلۡطٰنٍ ۚ بَلۡ  کُنۡتُمۡ  قَوۡمًا طٰغِیۡنَ ﴿۳۰﴾ ",
    "ayat_id": 3854,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "پس ہم پر ہمارے رب کی بات ثابت ہوگئی، بے شک ہم البتہ (مزہ) چکھنے والے ہیں۔",
    "arabic":
        "فَحَقَّ عَلَیۡنَا قَوۡلُ رَبِّنَاۤ ٭ۖ اِنَّا  لَذَآئِقُوۡنَ ﴿۳۱﴾ ",
    "ayat_id": 3855,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پس ہم نے تمہیں بہکایا بے شک ہم (خود) گمراہ تھے۔",
    "arabic": "فَاَغۡوَیۡنٰکُمۡ   اِنَّا کُنَّا غٰوِیۡنَ ﴿۳۲﴾ ",
    "ayat_id": 3856,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پس بے شک وہ اس دن عذاب میں (بھی) شریک رہیں گے۔ ",
    "arabic": "فَاِنَّہُمۡ یَوۡمَئِذٍ فِی الۡعَذَابِ مُشۡتَرِکُوۡنَ ﴿۳۳﴾ ",
    "ayat_id": 3857,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک ہم اسی طرح کرتے ہیں مجرموں کے ساتھ ۔",
    "arabic": "اِنَّا کَذٰلِکَ نَفۡعَلُ  بِالۡمُجۡرِمِیۡنَ ﴿۳۴﴾ ",
    "ayat_id": 3858,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "بے شک جب اُن سے کہا جاتا تھا کہ اللہ کے سوا کوئی معبود نہیں (تو) وہ تکبّر کرتے تھے۔",
    "arabic":
        "اِنَّہُمۡ  کَانُوۡۤا  اِذَا  قِیۡلَ  لَہُمۡ  لَاۤ  اِلٰہَ   اِلَّا  اللّٰہُ ۙ یَسۡتَکۡبِرُوۡنَ ﴿ۙ۳۵﴾ ",
    "ayat_id": 3859,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اور وہ کہتے ہیں کیا ہم اپنے معبودوں کو چھوڑ دیں؟ ایک شاعر، دیوانے کی خاطر۔",
    "arabic":
        "وَ یَقُوۡلُوۡنَ  اَئِنَّا  لَتَارِکُوۡۤا  اٰلِہَتِنَا لِشَاعِرٍ  مَّجۡنُوۡنٍ ﴿ؕ۳۶﴾ ",
    "ayat_id": 3860,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "بلکہ وہ (ﷺ)  حق کے ساتھ آئے ہیں اور وہ (ﷺ)  تصدیق کرتے ہیں رسولوں کی۔",
    "arabic": "بَلۡ جَآءَ بِالۡحَقِّ وَ صَدَّقَ الۡمُرۡسَلِیۡنَ ﴿۳۷﴾ ",
    "ayat_id": 3861,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک تم دردناک عذاب ضرور چکھنے والے ہو۔",
    "arabic": "اِنَّکُمۡ  لَذَآئِقُوا  الۡعَذَابِ  الۡاَلِیۡمِ ﴿ۚ۳۸﴾ ",
    "ayat_id": 3862,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور تمہیں بدلہ نہ دیا جائے گا مگر (اس کے مطابق) جو تم کرتے تھے۔",
    "arabic": "وَ مَا تُجۡزَوۡنَ  اِلَّا مَا کُنۡتُمۡ تَعۡمَلُوۡنَ ﴿ۙ۳۹﴾ ",
    "ayat_id": 3863,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "(ہاں) مگر اللہ کے خاص کیے ہوئے (چُنے ہوئے) بندے۔",
    "arabic": "اِلَّا عِبَادَ  اللّٰہِ  الۡمُخۡلَصِیۡنَ ﴿۴۰﴾ ",
    "ayat_id": 3864,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "ان کے لیے رزقِ معلوم (مقرر)  ہے۔",
    "arabic": "اُولٰٓئِکَ لَہُمۡ  رِزۡقٌ  مَّعۡلُوۡمٌ ﴿ۙ۴۱﴾ ",
    "ayat_id": 3865,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "(یعنی) میوے، اور وہ اعزاز والے ہوں گے،",
    "arabic": "فَوَاکِہُ ۚ وَ  ہُمۡ  مُّکۡرَمُوۡنَ ﴿ۙ۴۲﴾ ",
    "ayat_id": 3866,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "نعمت کے باغات میں،",
    "arabic": "فِیۡ   جَنّٰتِ  النَّعِیۡمِ ﴿ۙ۴۳﴾ ",
    "ayat_id": 3867,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تختوں پر آمنے سامنے۔",
    "arabic": "عَلٰی  سُرُرٍ  مُّتَقٰبِلِیۡنَ ﴿۴۴﴾ ",
    "ayat_id": 3868,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "دَورہ ہوگا اُن کے آگے بہتے ہوئے (صاف) مشروب کے جام کا۔",
    "arabic": "یُطَافُ عَلَیۡہِمۡ  بِکَاۡسٍ مِّنۡ  مَّعِیۡنٍۭ ﴿ۙ۴۵﴾ ",
    "ayat_id": 3869,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "سفید رنگ کا، پینے والوں کے لیے لذّت (دینے والا)۔",
    "arabic": "بَیۡضَآءَ   لَذَّۃٍ   لِّلشّٰرِبِیۡنَ ﴿ۚ ۖ۴۶﴾ ",
    "ayat_id": 3870,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "نہ اس میں دردِ سر ہوگا اور نہ وہ اس سے بہکی بہکی باتیں کریں گے۔",
    "arabic": "لَا فِیۡہَا غَوۡلٌ وَّ لَا ہُمۡ عَنۡہَا یُنۡزَفُوۡنَ ﴿۴۷﴾ ",
    "ayat_id": 3871,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور اُن کے پاس ہوںگی نیچی نگاہوں والیاں، بڑی بڑی آنکھوں والیاں،",
    "arabic": "وَ عِنۡدَہُمۡ  قٰصِرٰتُ الطَّرۡفِ عِیۡنٌ ﴿ۙ۴۸﴾ ",
    "ayat_id": 3872,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "گویا وہ انڈے ہیں پوشیدہ رکھے ہوئے۔",
    "arabic": "کَاَنَّہُنَّ بَیۡضٌ مَّکۡنُوۡنٌ ﴿۴۹﴾ ",
    "ayat_id": 3873,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پھر اُن میں سے ایک دوسرے کی طرف با ہم سوال کرتے ہوئے رُخ کرے گا۔",
    "arabic": "فَاَقۡبَلَ بَعۡضُہُمۡ عَلٰی بَعۡضٍ یَّتَسَآءَلُوۡنَ ﴿۵۰﴾ ",
    "ayat_id": 3874,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "ان میں سے ایک کہنے والا کہے گا بے شک (دنیا میں) میرا ایک ہم نشین تھا۔",
    "arabic": "قَالَ  قَآئِلٌ مِّنۡہُمۡ  اِنِّیۡ کَانَ لِیۡ قَرِیۡنٌ ﴿ۙ۵۱﴾ ",
    "ayat_id": 3875,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "وہ کہا کرتا تھا کیا تو (قیامت کو) سچ ماننے والوں میں سے ہے؟",
    "arabic": "یَّقُوۡلُ  اَئِنَّکَ  لَمِنَ  الۡمُصَدِّقِیۡنَ ﴿۵۲﴾ ",
    "ayat_id": 3876,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "کیا جب ہم مر گئے، اور ہم ہو گئے مٹی اور ہڈیاں، کیا ہمیں بدلہ دیا جائے گا؟",
    "arabic":
        "ءَ اِذَا مِتۡنَا وَ کُنَّا تُرَابًا وَّ  عِظَامًا  ءَ اِنَّا لَمَدِیۡنُوۡنَ ﴿۵۳﴾ ",
    "ayat_id": 3877,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        " وہ کہے گا کیا تم جھانکنے والے ہو (دوزخی کو جھانک کر دیکھ سکتے ہو؟) ",
    "arabic": "قَالَ  ہَلۡ  اَنۡتُمۡ  مُّطَّلِعُوۡنَ ﴿۵۴﴾ ",
    "ayat_id": 3878,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تو وہ جھانکے گا تو اسے دیکھے گا دوزخ کے درمیان میں۔",
    "arabic": "فَاطَّلَعَ  فَرَاٰہُ  فِیۡ  سَوَآءِ  الۡجَحِیۡمِ ﴿۵۵﴾ ",
    "ayat_id": 3879,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "وہ کہے گا، اللہ کی قسم! قریب تھا کہ تو مجھے ہلاک کر ڈالے۔",
    "arabic": "قَالَ تَاللّٰہِ   اِنۡ  کِدۡتَّ  لَتُرۡدِیۡنِ ﴿ۙ۵۶﴾ ",
    "ayat_id": 3880,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اور اگر میرے رب کی نعمت نہ ہوتی تو میں ضرور (عذاب کے لیے) حاضر کیے جانے والوں میں ہوتا",
    "arabic":
        "وَ لَوۡ لَا نِعۡمَۃُ رَبِّیۡ  لَکُنۡتُ مِنَ الۡمُحۡضَرِیۡنَ ﴿۵۷﴾ ",
    "ayat_id": 3881,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پس پہلی موت کے سوائے ہم مرنے والوں میں سے نہیں",
    "arabic": "اَفَمَا نَحۡنُ بِمَیِّتِیۡنَ ﴿ۙ۵۸﴾ ",
    "ayat_id": 3882,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        " (آئندہ ہم پر کوئی موت نہ آئے گی) اور نہ ہم عذاب دیئے جانے والوں میں سے ہوں گے۔",
    "arabic":
        "اِلَّا مَوۡتَتَنَا الۡاُوۡلٰی وَ مَا نَحۡنُ بِمُعَذَّبِیۡنَ ﴿۵۹﴾ ",
    "ayat_id": 3883,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک یہی ہے عظیم کامیابی۔",
    "arabic": "اِنَّ ہٰذَا  لَہُوَ  الۡفَوۡزُ  الۡعَظِیۡمُ ﴿۶۰﴾ ",
    "ayat_id": 3884,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پس اس جیسی نعمت کے لیے چاہیے ضرور عمل کریں عمل کرنے والے۔",
    "arabic": "لِمِثۡلِ ہٰذَا فَلۡیَعۡمَلِ الۡعٰمِلُوۡنَ ﴿۶۱﴾ ",
    "ayat_id": 3885,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": " کیا یہ بہتر ضیافت ہے؟ یا تھوہر کا درخت؟",
    "arabic": "اَذٰلِکَ خَیۡرٌ  نُّزُلًا  اَمۡ شَجَرَۃُ  الزَّقُّوۡمِ ﴿۶۲﴾ ",
    "ayat_id": 3886,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک ہم نے اس کو ایک آزمائش بنایا ہے ظالموں کے لیے۔",
    "arabic": "اِنَّا جَعَلۡنٰہَا فِتۡنَۃً  لِّلظّٰلِمِیۡنَ ﴿۶۳﴾ ",
    "ayat_id": 3887,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": " بے شک وہ ایک درخت ہے جہنم کی جڑ(گہرائی) میں نکلتا ہے",
    "arabic": "اِنَّہَا شَجَرَۃٌ  تَخۡرُجُ فِیۡۤ  اَصۡلِ الۡجَحِیۡمِ ﴿ۙ۶۴﴾ ",
    "ayat_id": 3888,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": " اس کا خوشہ،گویا کہ وہ شیطانوں کے سر (سانپوں کے پھن)ہیں۔",
    "arabic": "طَلۡعُہَا کَاَنَّہٗ  رُءُوۡسُ الشَّیٰطِیۡنِ ﴿۶۵﴾ ",
    "ayat_id": 3889,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک وہ اس سے کھائیں گے، سو اس سے پیٹ بھریں گے۔",
    "arabic":
        "فَاِنَّہُمۡ لَاٰکِلُوۡنَ مِنۡہَا فَمَالِئُوۡنَ مِنۡہَا الۡبُطُوۡنَ ﴿ؕ۶۶﴾ ",
    "ayat_id": 3890,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        " پھر بے شک اس (کھانے) پر ان کے لیے کھولتا ہوا پانی (پِیپ) مِلامِلا کر (دیا جائے گا)",
    "arabic": "ثُمَّ  اِنَّ لَہُمۡ عَلَیۡہَا لَشَوۡبًا مِّنۡ حَمِیۡمٍ ﴿ۚ۶۷﴾ ",
    "ayat_id": 3891,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پھر ان کی بازگشت جہنم ہی کی طرف ہوگی۔",
    "arabic": "ثُمَّ   اِنَّ  مَرۡجِعَہُمۡ  لَا۠ اِلَی  الۡجَحِیۡمِ ﴿۶۸﴾ ",
    "ayat_id": 3892,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک انہوں نے اپنے باپ دادا کو گمراہ پایا تھا۔",
    "arabic": "اِنَّہُمۡ  اَلۡفَوۡا  اٰبَآءَہُمۡ  ضَآلِّیۡنَ ﴿ۙ۶۹﴾ ",
    "ayat_id": 3893,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "سو وہ ان کے نقشِ قدم پر دوڑے جاتے تھے۔",
    "arabic": "فَہُمۡ عَلٰۤی  اٰثٰرِہِمۡ  یُہۡرَعُوۡنَ ﴿۷۰﴾ ",
    "ayat_id": 3894,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور تحقیق ان سے پہلے گمراہ ہوئے تھے (ان کے) اگلوں میں سے اکثر۔",
    "arabic": "وَ لَقَدۡ ضَلَّ  قَبۡلَہُمۡ  اَکۡثَرُ الۡاَوَّلِیۡنَ ﴿ۙ۷۱﴾ ",
    "ayat_id": 3895,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور تحقیق ہم نے ان میں ڈرانے والے (رسول) بھیجے تھے۔",
    "arabic": "وَ لَقَدۡ اَرۡسَلۡنَا فِیۡہِمۡ مُّنۡذِرِیۡنَ ﴿۷۲﴾ ",
    "ayat_id": 3896,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "سو آپ (ﷺ) دیکھیں کیسا ہوا ان کا انجام جنہیں ڈرایا گیا تھا؟",
    "arabic": "فَانۡظُرۡ کَیۡفَ کَانَ عَاقِبَۃُ  الۡمُنۡذَرِیۡنَ ﴿ۙ۷۳﴾ ",
    "ayat_id": 3897,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "(ہاں) مگر اللہ کے خاص کیے ہوئے بندے (بندگانِ خاص کا انجام کتنا اچھا ہوا)۔",
    "arabic": "اِلَّا عِبَادَ  اللّٰہِ  الۡمُخۡلَصِیۡنَ ﴿٪۷۴﴾ ",
    "ayat_id": 3898,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اور تحقیق نوح (علیہ السلام) نے ہمیں پکارا سو ہم خوب دعا قبول کرنے والے ہیں۔",
    "arabic": "وَ لَقَدۡ نَادٰىنَا نُوۡحٌ فَلَنِعۡمَ الۡمُجِیۡبُوۡنَ ﴿۫ ۖ۷۵﴾ ",
    "ayat_id": 3899,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور ہم نے اسے اور اس کے گھر والوں کو بڑی مصیبت سے نجات دی۔",
    "arabic":
        "وَ نَجَّیۡنٰہُ  وَ اَہۡلَہٗ  مِنَ الۡکَرۡبِ الۡعَظِیۡمِ ﴿۫ ۖ۷۶﴾ ",
    "ayat_id": 3900,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور ہم نے کیا اس کی اولاد کو باقی رہنے والی۔",
    "arabic": "وَ جَعَلۡنَا ذُرِّیَّتَہٗ  ہُمُ  الۡبٰقِیۡنَ ﴿۫ ۖ۷۷﴾ ",
    "ayat_id": 3901,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور ہم نے اس کا (ذکرِ خیر) بعد میں آنے والوں میں چھوڑا۔",
    "arabic": "وَ تَرَکۡنَا عَلَیۡہِ  فِی الۡاٰخِرِیۡنَ ﴿۫ ۖ۷۸﴾ ",
    "ayat_id": 3902,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "نوح (علیہ السلام) پر سلام ہو سارے جہانوں میں۔",
    "arabic": "سَلٰمٌ  عَلٰی نُوۡحٍ فِی الۡعٰلَمِیۡنَ ﴿۷۹﴾ ",
    "ayat_id": 3903,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک ہم اسی طرح نیکو کاروں کو جزا دیتے ہیں۔",
    "arabic": "اِنَّا کَذٰلِکَ نَجۡزِی الۡمُحۡسِنِیۡنَ ﴿۸۰﴾ ",
    "ayat_id": 3904,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک وہ ہمارے مؤمن بندوں میں سے تھے۔",
    "arabic": "اِنَّہٗ  مِنۡ عِبَادِنَا الۡمُؤۡمِنِیۡنَ ﴿۸۱﴾ ",
    "ayat_id": 3905,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پھر ہم نے دوسروں کو غرق کر دیا۔",
    "arabic": "ثُمَّ   اَغۡرَقۡنَا الۡاٰخَرِیۡنَ  ﴿۸۲﴾",
    "ayat_id": 3906,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اور بے شک ابراہیم (علیہ السلام) اسی کے طریقے پر چلنے والوں میں سے تھے۔",
    "arabic": "وَ  اِنَّ مِنۡ شِیۡعَتِہٖ  لَاِبۡرٰہِیۡمَ ﴿ۘ۸۳﴾ ",
    "ayat_id": 3907,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "جب وہ اپنے رب کے پاس آئے صاف دل کے ساتھ ، (عیب سے پاک دل کے ساتھ)،",
    "arabic": "اِذۡ  جَآءَ  رَبَّہٗ  بِقَلۡبٍ  سَلِیۡمٍ ﴿۸۴﴾ ",
    "ayat_id": 3908,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "(یاد کرو) جب اس نے اپنے باپ اور اپنی قوم سے کہا تم کس (واہیات) چیز کی پرستش کرتے ہو؟",
    "arabic": "اِذۡ قَالَ  لِاَبِیۡہِ وَ قَوۡمِہٖ مَاذَا تَعۡبُدُوۡنَ ﴿ۚ۸۵﴾ ",
    "ayat_id": 3909,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "کیا تم اللہ کے سوا جھوٹ موٹ کے معبود چاہتے ہو ؟",
    "arabic": "اَئِفۡکًا  اٰلِہَۃً  دُوۡنَ اللّٰہِ  تُرِیۡدُوۡنَ ﴿ؕ۸۶﴾ ",
    "ayat_id": 3910,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "سو تمام جہانوں کے پروردگار کے بارے میں تمہارا کیا گمان ہے؟",
    "arabic": "فَمَا ظَنُّکُمۡ  بِرَبِّ الۡعٰلَمِیۡنَ ﴿۸۷﴾ ",
    "ayat_id": 3911,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پھر اس نے ستاروں کو ایک نظر دیکھا۔",
    "arabic": "فَنَظَرَ  نَظۡرَۃً  فِی النُّجُوۡمِ ﴿ۙ۸۸﴾ ",
    "ayat_id": 3912,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تو اس نے کہا بے شک میں بیمار ہوں۔",
    "arabic": "فَقَالَ  اِنِّیۡ سَقِیۡمٌ ﴿۸۹﴾ ",
    "ayat_id": 3913,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پس وہ پیٹھ پھیر کر اس سے پھر گئے۔",
    "arabic": "فَتَوَلَّوۡا عَنۡہُ  مُدۡبِرِیۡنَ ﴿۹۰﴾ ",
    "ayat_id": 3914,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "پھر وہ ان کے معبودوں میں چھپ کر گُھس گیا، پھر وہ (بطور تمسخر) کہنے لگا کیا تم کھاتے نہیں؟",
    "arabic":
        "فَرَاغَ  اِلٰۤی  اٰلِہَتِہِمۡ  فَقَالَ  اَلَا  تَاۡکُلُوۡنَ ﴿ۚ۹۱﴾ ",
    "ayat_id": 3915,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تمہیں کیا ہوا؟ تم بولتے نہیں؟",
    "arabic": "مَا  لَکُمۡ  لَا تَنۡطِقُوۡنَ ﴿۹۲﴾ ",
    "ayat_id": 3916,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پھر وہ پوری قوت سے مارتا ہوا ان پر جا پڑا۔",
    "arabic": "فَرَاغَ  عَلَیۡہِمۡ  ضَرۡبًۢا بِالۡیَمِیۡنِ ﴿۹۳﴾ ",
    "ayat_id": 3917,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": " پھر وہ (بت پرست) متوجہ ہوئے اس کی طرف دوڑتے ہوئے (آئے)۔",
    "arabic": "فَاَقۡبَلُوۡۤا  اِلَیۡہِ  یَزِفُّوۡنَ ﴿۹۴﴾ ",
    "ayat_id": 3918,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اس نے فرمایا کیا تم (ان کی) پرستش کرتے ہو؟ جو تم خود تراشتے ہو ۔",
    "arabic": "قَالَ  اَتَعۡبُدُوۡنَ  مَا تَنۡحِتُوۡنَ ﴿ۙ۹۵﴾ ",
    "ayat_id": 3919,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "حالانکہ اللہ نے تمہیں پیدا کیا، اور جو تم کرتے (بناتے) ہو۔",
    "arabic": "وَ اللّٰہُ خَلَقَکُمۡ  وَ مَا تَعۡمَلُوۡنَ ﴿۹۶﴾ ",
    "ayat_id": 3920,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "انہوں نے (ایک دوسرے کو) کہا اس کے لیے ایک عمارت (آتش خانہ) بناؤ، پھر اسے آگ میں ڈال دو،",
    "arabic":
        "قَالُوا ابۡنُوۡا لَہٗ  بُنۡیَانًا فَاَلۡقُوۡہُ  فِی الۡجَحِیۡمِ ﴿۹۷﴾ ",
    "ayat_id": 3921,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پھر انہوں نے اس پر داؤ کرنا چاہا تو ہم نے انہیں زیر کردیا۔",
    "arabic": "فَاَرَادُوۡا بِہٖ کَیۡدًا فَجَعَلۡنٰہُمُ  الۡاَسۡفَلِیۡنَ ﴿۹۸﴾ ",
    "ayat_id": 3922,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اور ابراہیم (علیہ السلام) نے کہا میں اپنے رب کی طرف جانے والا ہوں وہ عنقریب مجھے بھی راہ دکھائے گا۔",
    "arabic": "وَ قَالَ  اِنِّیۡ ذَاہِبٌ  اِلٰی رَبِّیۡ سَیَہۡدِیۡنِ ﴿۹۹﴾ ",
    "ayat_id": 3923,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اے میرے رب! مجھے صالحین میں سے (نیک اولاد) عطا فرما۔",
    "arabic": "رَبِّ ہَبۡ  لِیۡ  مِنَ  الصّٰلِحِیۡنَ ﴿۱۰۰﴾ ",
    "ayat_id": 3924,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پس ہم نے اسے ایک بُردبار لڑکے کی بشارت دی۔",
    "arabic": "فَبَشَّرۡنٰہُ  بِغُلٰمٍ  حَلِیۡمٍ ﴿۱۰۱﴾ ",
    "ayat_id": 3925,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "پھر جب وہ اس کے ساتھ دوڑنے (کی عمر کو) پہنچا تو ابراہیم (علیہ السلام) نے کہا اے میرے بیٹے! بے شک میں خواب میں دیکھتا ہوں کہ میں تجھے ذبح کر رہا ہوں۔ اب تو دیکھ تیری کیا رائے ہے؟ اس نے کہا اے میرے ابا جان! آپ کو جو حکم کیا جاتا ہے وہ کریں، آپ مجھے جلد ہی پائیں گے انشاء اللہ (اگر اللہ نے چاہا)  صبر کرنے والوں میں سے۔",
    "arabic":
        "فَلَمَّا بَلَغَ مَعَہُ  السَّعۡیَ قَالَ یٰبُنَیَّ  اِنِّیۡۤ اَرٰی فِی الۡمَنَامِ اَنِّیۡۤ  اَذۡبَحُکَ فَانۡظُرۡ مَاذَا تَرٰی ؕ قَالَ یٰۤاَبَتِ افۡعَلۡ مَا تُؤۡمَرُ ۫ سَتَجِدُنِیۡۤ  اِنۡ شَآءَ اللّٰہُ مِنَ الصّٰبِرِیۡنَ ﴿۱۰۲﴾ ",
    "ayat_id": 3926,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "پس جب دونوں نے حکمِ الٰہی کو مان لیا، باپ نے بیٹے کو پیشانی کے بل لٹایا",
    "arabic": "فَلَمَّاۤ   اَسۡلَمَا وَ  تَلَّہٗ   لِلۡجَبِیۡنِ ﴿۱۰۳﴾ۚ ",
    "ayat_id": 3927,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور ہم نے اس کو پکارا کہ اے ابراہیم(علیہ السلام) !",
    "arabic": "وَ  نَادَیۡنٰہُ  اَنۡ  یّٰۤاِبۡرٰہِیۡمُ ﴿۱۰۴﴾ۙ",
    "ayat_id": 3928,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "تحقیق تو نے خواب کو سچ کر دکھایا، بے شک ہم نیکوکاروں کو اسی طرح جزا دیا کرتے ہیں۔",
    "arabic":
