    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پھر ہم نے انہیں چٹیل میدان میں پھینک دیا اور وہ بیمار تھے۔",
    "arabic": "فَنَبَذۡنٰہُ  بِالۡعَرَآءِ  وَ  ہُوَ  سَقِیۡمٌ ﴿۱۴۵﴾ۚ ",
    "ayat_id": 3969,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور ہم نے اُگایا اُن پر ایک بیلدار (کدو کا) پودا۔",
    "arabic": "وَ اَنۡۢبَتۡنَا عَلَیۡہِ شَجَرَۃً مِّنۡ یَّقۡطِیۡنٍ ﴿۱۴۶﴾ۚ ",
    "ayat_id": 3970,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور ہم نے انہیں ایک لاکھ یا اس سے زیادہ لوگوں کی طرف بھیجا۔",
    "arabic":
        "وَ اَرۡسَلۡنٰہُ  اِلٰی مِائَۃِ  اَلۡفٍ اَوۡ یَزِیۡدُوۡنَ ﴿۱۴۷﴾ۚ ",
    "ayat_id": 3971,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "سو وہ لوگ ایمان لائے اور ہم نے انہیں ایک مد ت تک کے لیے بہرہ مند کیا۔",
    "arabic": "فَاٰمَنُوۡا  فَمَتَّعۡنٰہُمۡ   اِلٰی حِیۡنٍ ﴿۱۴۸﴾ؕ ",
    "ayat_id": 3972,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "پس آپ (ﷺ) اُن سے پوچھیں کیا تیرے رب کے لیے بیٹیاں ہیں اور ان کے لیے بیٹے ہیں؟",
    "arabic":
        "فَاسۡتَفۡتِہِمۡ  اَلِرَبِّکَ الۡبَنَاتُ وَ لَہُمُ الۡبَنُوۡنَ ﴿۱۴۹﴾ۙ ",
    "ayat_id": 3973,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "کیا ہم نے فرشتوں کو عورت (ذات) پیدا کیا ہے؟ اور وہ دیکھ رہے تھے؟",
    "arabic":
        "اَمۡ خَلَقۡنَا الۡمَلٰٓئِکَۃَ  اِنَاثًا  وَّ ہُمۡ شٰہِدُوۡنَ ﴿۱۵۰﴾ ",
    "ayat_id": 3974,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "یاد رکھو، بے شک وہ اپنی بہتان طرازی سے کہتے ہیں۔",
    "arabic": "اَلَاۤ  اِنَّہُمۡ  مِّنۡ  اِفۡکِہِمۡ  لَیَقُوۡلُوۡنَ ﴿۱۵۱﴾ۙ ",
    "ayat_id": 3975,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "(کہ) اللہ صاحبِ اولاد ہے، اور وہ بے شک بلتحقیق جھوٹے ہیں۔",
    "arabic": "وَلَدَ اللّٰہُ ۙ وَ  اِنَّہُمۡ  لَکٰذِبُوۡنَ ﴿۱۵۲﴾ ",
    "ayat_id": 3976,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "کیا اس نے بیٹیوں کو بیٹیوں پر پسند کیا ہے؟",
    "arabic": "اَصۡطَفَی الۡبَنَاتِ عَلَی الۡبَنِیۡنَ ﴿۱۵۳﴾ؕ ",
    "ayat_id": 3977,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تمہیں کیا ہوگیا ہے؟ تم کیسا فیصلہ کرتے ہو؟",
    "arabic": "مَا  لَکُمۡ ۟  کَیۡفَ تَحۡکُمُوۡنَ ﴿۱۵۴﴾ ",
    "ayat_id": 3978,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تو کیا تم غور نہیں کرتے؟",
    "arabic": "اَفَلَا  تَذَکَّرُوۡنَ ﴿۱۵۵﴾ۚ ",
    "ayat_id": 3979,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "کیا تمہارے پاس کوئی کُھلی سند ہے؟",
    "arabic": "اَمۡ  لَکُمۡ  سُلۡطٰنٌ  مُّبِیۡنٌ ﴿۱۵۶﴾ۙ ",
    "ayat_id": 3980,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تو اپنی وہ کتاب لے آؤ اگر تم سچے ہو۔",
    "arabic": "فَاۡتُوۡا بِکِتٰبِکُمۡ  اِنۡ کُنۡتُمۡ صٰدِقِیۡنَ ﴿۱۵۷﴾ ",
    "ayat_id": 3981,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اور انہوں نے اس کے اور جنات کے درمیان ایک رشتہ ٹھہرایا، اور تحقیق جان لیا جنّات نے کہ بے شک وہ (عذاب میں) حاضر  (گرفتار) کیے جائیں گے۔",
    "arabic":
        "وَ جَعَلُوۡا بَیۡنَہٗ  وَ بَیۡنَ الۡجِنَّۃِ  نَسَبًا ؕ وَ لَقَدۡ عَلِمَتِ الۡجِنَّۃُ  اِنَّہُمۡ لَمُحۡضَرُوۡنَ ﴿۱۵۸﴾ۙ ",
    "ayat_id": 3982,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اللہ اس سے پاک ہے جو بیان کرتے ہیں۔",
    "arabic": "سُبۡحٰنَ اللّٰہِ عَمَّا یَصِفُوۡنَ ﴿۱۵۹﴾ۙ ",
    "ayat_id": 3983,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "سوائے اللہ چنے ہوئے بندے۔",
    "arabic": "اِلَّا عِبَادَ  اللّٰہِ  الۡمُخۡلَصِیۡنَ ﴿۱۶۰﴾ ",
    "ayat_id": 3984,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تو بے شک تم اور وہ جن کی تم پرستش کرتے ہو،",
    "arabic": "فَاِنَّکُمۡ  وَ مَا  تَعۡبُدُوۡنَ ﴿۱۶۱﴾ۙ ",
    "ayat_id": 3985,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "وہ اس کے (اللہ کے) خلاف (کسی کو) نہیں بہکا سکتے،",
    "arabic": "مَاۤ   اَنۡتُمۡ  عَلَیۡہِ  بِفٰتِنِیۡنَ ﴿۱۶۲﴾ۙ ",
    "ayat_id": 3986,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اس کے سوا جو جہنم میں جانے والا ہے۔",
    "arabic": "اِلَّا  مَنۡ  ہُوَ  صَالِ الۡجَحِیۡمِ ﴿۱۶۳﴾ ",
    "ayat_id": 3987,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اور (فرشتوں نے کہا) ہم میں سے کوئی بھی ایسا نہیں جس کا ایک معین درجہ نہ ہو،",
    "arabic": "وَ مَا مِنَّاۤ   اِلَّا  لَہٗ  مَقَامٌ  مَّعۡلُوۡمٌ ﴿۱۶۴﴾ۙ ",
    "ayat_id": 3988,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور بے شک ہم ہی صف بستہ رہنے والے ہیں۔",
    "arabic": "وَّ  اِنَّا  لَنَحۡنُ الصَّآفُّوۡنَ ﴿۱۶۵﴾ۚ ",
    "ayat_id": 3989,
    "surah_id": 37,
    "surah_name": "As-Saaffat",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور بے شک ہم ہی تسبیح کرنے والے ہیں۔",
    "arabic": "وَ  اِنَّا لَنَحۡنُ الۡمُسَبِّحُوۡنَ ﴿۱۶۶﴾ ",
