    "arabic": "وَ عِنۡدَہُمۡ  قٰصِرٰتُ الطَّرۡفِ  اَتۡرَابٌ ﴿۵۲﴾ ",
    "ayat_id": 4059,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "یہ ہے جس کا تم سے وعدہ کیا جاتا ہے روزِ حساب کے لیے۔",
    "arabic": "ہٰذَا مَا تُوۡعَدُوۡنَ  لِیَوۡمِ الۡحِسَابِ ﴿ؓ۵۳﴾  ",
    "ayat_id": 4060,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک یہ ہمارا رزق ہے اس کو (کبھی) ختم ہونا نہیں۔ یہ ہے (جزا)۔",
    "arabic": "اِنَّ  ہٰذَا  لَرِزۡقُنَا مَا  لَہٗ  مِنۡ  نَّفَادٍ  ﴿ۚ ۖ۵۴﴾",
    "ayat_id": 4061,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور بے شک سرکشوں کے لیے البتہ بُرا ٹھکانا ہے",
    "arabic": "ہٰذَا ؕ وَ  اِنَّ  لِلطّٰغِیۡنَ  لَشَرَّ  مَاٰبٍ ﴿ۙ۵۵﴾ ",
    "ayat_id": 4062,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "(یعنی) جہنم، جس میں وہ داخل ہوں گے، سو بُرا ہے فرش (اُن کی آرامگاہ)",
    "arabic": "جَہَنَّمَ ۚ یَصۡلَوۡنَہَا ۚ فَبِئۡسَ الۡمِہَادُ ﴿۵۶﴾ ",
    "ayat_id": 4063,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "یہ کھولتا ہوا پانی اور پیپ ہے پس تم اس کو چکھو،",
    "arabic": "ہٰذَا ۙ فَلۡیَذُوۡقُوۡہُ حَمِیۡمٌ  وَّ غَسَّاقٌ ﴿ۙ۵۷﴾ ",
    "ayat_id": 4064,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور اس کے علاوہ اس کی شکل کی کئی قسمیں ہوں گی۔",
    "arabic": "وَّ اٰخَرُ  مِنۡ شَکۡلِہٖۤ  اَزۡوَاجٌ ﴿ؕ۵۸﴾ ",
    "ayat_id": 4065,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "یہ ایک جماعت ہے تمہارے ساتھ (جہنم میں) داخل ہو رہے ہیں، انہیں کوئی فراخی نہ ہو، بے شک وہ جہنم میں داخل ہونے والے ہیں۔",
    "arabic":
        "ہٰذَا فَوۡجٌ مُّقۡتَحِمٌ  مَّعَکُمۡ ۚ لَا مَرۡحَبًۢا بِہِمۡ ؕ اِنَّہُمۡ صَالُوا النَّارِ ﴿۵۹﴾ ",
    "ayat_id": 4066,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "وہ کہیں گے بلکہ تمیں کوئی فراخی نہ ہو، بے شک تم ہی ہمارے لیے یہ (مصیبت) آگے لائے ہو، سو بُرا ہے ٹھکانا۔",
    "arabic":
        "قَالُوۡا بَلۡ  اَنۡتُمۡ ۟ لَا مَرۡحَبًۢا بِکُمۡ ؕ اَنۡتُمۡ  قَدَّمۡتُمُوۡہُ   لَنَا ۚ فَبِئۡسَ الۡقَرَارُ ﴿۶۰﴾ ",
    "ayat_id": 4067,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "وہ کہیں گے، اے ہمارے رب! جو ہمارے لیے یہ (مصیبت) آگے لایا ہے تو جہنم میں (اس کے لیے) عذاب دوچند کردے۔",
    "arabic":
        "قَالُوۡا رَبَّنَا مَنۡ قَدَّمَ لَنَا ہٰذَا فَزِدۡہُ عَذَابًا ضِعۡفًا فِی النَّارِ ﴿۶۱﴾ ",
    "ayat_id": 4068,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اور وہ کہیں گے ہمیں کیا ہوا؟ ہم (دوزخ میں) ان لوگوں کو نہیں دیکھتے جنہیں ہم بُرے لوگوں میں شمار کرتے تھے۔",
    "arabic":
        "وَ قَالُوۡا مَا لَنَا لَا نَرٰی رِجَالًا کُنَّا نَعُدُّہُمۡ مِّنَ الۡاَشۡرَارِ ﴿ؕ۶۲﴾ ",
    "ayat_id": 4069,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "کیا ہم نے انہیں ٹھٹھے میں پکڑا تھا؟ یا کج ہو گئی ہیں ان سے (ہماری) آنکھیں؟",
    "arabic":
        "اَتَّخَذۡنٰہُمۡ سِخۡرِیًّا  اَمۡ  زَاغَتۡ عَنۡہُمُ الۡاَبۡصَارُ ﴿۶۳﴾ ",
    "ayat_id": 4070,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "بے شک اہلِ دوزخ کا باہم یہ جھگڑنا بالکل سچ ہے۔",
    "arabic": "اِنَّ  ذٰلِکَ  لَحَقٌّ  تَخَاصُمُ   اَہۡلِ النَّارِ ﴿٪۶۴﴾ ",
    "ayat_id": 4071,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "آپ (ﷺ) فرما دیں اس کے سوا نہیں کہ میں ڈرانے والا ہوں اور اللہ کے سوا کوئی معبود نہیں، وہ یکتا، زبردست ہے۔",
    "arabic":
        "قُلۡ  اِنَّمَاۤ  اَنَا مُنۡذِرٌ ٭ۖ وَّ مَا مِنۡ  اِلٰہٍ   اِلَّا اللّٰہُ  الۡوَاحِدُ  الۡقَہَّارُ ﴿ۚ۶۵﴾ ",
    "ayat_id": 4072,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "پروردگار ہے آسمانوں کا اور زمین کا، اور جو ان دونوں کے درمیان ہے غالب، بڑا بخشنے والا ہے۔",
    "arabic":
        "رَبُّ السَّمٰوٰتِ وَ الۡاَرۡضِ وَ مَا بَیۡنَہُمَا الۡعَزِیۡزُ  الۡغَفَّارُ ﴿۶۶﴾ ",
    "ayat_id": 4073,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "آپ فرما دیں یہ ایک بڑی خبر ہے۔",
    "arabic": "قُلۡ ہُوَ  نَبَؤٌا عَظِیۡمٌ ﴿ۙ۶۷﴾ ",
    "ayat_id": 4074,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تم اس سے بے پرواہ ہو۔",
    "arabic": "اَنۡتُمۡ عَنۡہُ  مُعۡرِضُوۡنَ ﴿۶۸﴾ ",
    "ayat_id": 4075,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "مجھے کچھ خبر نہ تھی عالمِ بالا کی۔ (بلند قدر فرشتوں کی) جب وہ باہم جھگڑتے تھے۔",
    "arabic":
        "مَا کَانَ لِیَ مِنۡ عِلۡمٍۭ  بِالۡمَلَاِ الۡاَعۡلٰۤی  اِذۡ یَخۡتَصِمُوۡنَ ﴿۶۹﴾ ",
    "ayat_id": 4076,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "میری طرف اس کے سوا وحی نہیں کی جاتی کہ میں صاف صاف ڈرانے والا ہوں۔",
    "arabic":
        "اِنۡ یُّوۡحٰۤی اِلَیَّ  اِلَّاۤ  اَنَّمَاۤ  اَنَا نَذِیۡرٌ  مُّبِیۡنٌ ﴿۷۰﴾ ",
    "ayat_id": 4077,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "(یاد کرو) جب تمہارے رب نے کہا فرشتوں کو کہ میں مٹی سے ایک بشر پیدا کرنے والا ہوں۔",
    "arabic":
        "اِذۡ قَالَ رَبُّکَ لِلۡمَلٰٓئِکَۃِ  اِنِّیۡ خَالِقٌۢ  بَشَرًا مِّنۡ طِیۡنٍ ﴿۷۱﴾ ",
    "ayat_id": 4078,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "پھر جب میں اسے درست کر دوں اور اس میں اپنی روح  سے پھونک دوں، تو تم گر پڑو اس کے آگے سجدہ کرتے ہوئے۔",
    "arabic":
        "فَاِذَا سَوَّیۡتُہٗ وَ نَفَخۡتُ فِیۡہِ مِنۡ رُّوۡحِیۡ فَقَعُوۡا  لَہٗ   سٰجِدِیۡنَ ﴿۷۲﴾   ",
    "ayat_id": 4079,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "پس سب فرشتوں نے ااکٹھے سجدہ کیا۔",
    "arabic": "فَسَجَدَ الۡمَلٰٓئِکَۃُ  کُلُّہُمۡ  اَجۡمَعُوۡنَ ﴿ۙ۷۳﴾ ",
    "ayat_id": 4080,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "سوائے ابلیس کے کہ اس نے تکبّر کیا اور وہ ہوگیا کافروں میں سے۔",
    "arabic":
        "اِلَّاۤ  اِبۡلِیۡسَ ؕ اِسۡتَکۡبَرَ وَ کَانَ مِنَ الۡکٰفِرِیۡنَ ﴿۷۴﴾ ",
    "ayat_id": 4081,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "(اللہ نے) فرمایا اے ابلیس! اس کو سجدہ کرنے سے تجھے کس نے منع کیا (روکا) جسے میں نے اپنے ہاتھوں سے پیدا کیا؟ کیا تو نے تکبّر کیا (اپنے کو بڑا سمجھا) یا تو بلند درجہ والوں میں سے ہے؟",
    "arabic":
        "قَالَ یٰۤاِبۡلِیۡسُ مَا مَنَعَکَ اَنۡ تَسۡجُدَ لِمَا خَلَقۡتُ بِیَدَیَّ ؕ اَسۡتَکۡبَرۡتَ اَمۡ کُنۡتَ مِنَ  الۡعَالِیۡنَ ﴿۷۵﴾ ",
    "ayat_id": 4082,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اس نے کہا میں اس سے بہتر ہوں، تو نے مجھے آگ سے پیدا کیا اور اسے پیدا کیا مٹی سے۔",
    "arabic":
        "قَالَ  اَنَا خَیۡرٌ  مِّنۡہُ ؕ خَلَقۡتَنِیۡ  مِنۡ نَّارٍ وَّ  خَلَقۡتَہٗ  مِنۡ  طِیۡنٍ ﴿۷۶﴾ ",
    "ayat_id": 4083,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "(اللہ تعالیٰ نے)  فرمایا پس یہاں سے نکل جا کیونکہ تو راندۂ درگاہ ہے۔",
    "arabic": "قَالَ فَاخۡرُجۡ  مِنۡہَا فَاِنَّکَ  رَجِیۡمٌ ﴿ۚ ۖ۷۷﴾ ",
    "ayat_id": 4084,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اور بے شک تجھ پر میری لعنت رہے گی روزِ قیامت تک۔",
    "arabic": "وَّ اِنَّ عَلَیۡکَ لَعۡنَتِیۡۤ  اِلٰی یَوۡمِ الدِّیۡنِ ﴿۷۸﴾ ",
    "ayat_id": 4085,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu":
        "اس نے کہا اے میرے رب! مجھے اس دن تک مہلت دے جس دن (مُردے) اٹھائے جائیں گے۔",
    "arabic": "قَالَ رَبِّ فَاَنۡظِرۡنِیۡۤ  اِلٰی یَوۡمِ یُبۡعَثُوۡنَ ﴿۷۹﴾ ",
    "ayat_id": 4086,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "(اللہ نے) فرمایا پس تو وقتِ معین کے دن (روزِ قیامت)",
    "arabic": "قَالَ  فَاِنَّکَ مِنَ الۡمُنۡظَرِیۡنَ ﴿ۙ۸۰﴾ ",
    "ayat_id": 4087,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "تک مہلت دئیے جانے والوں میں سے ہے۔",
    "arabic": "اِلٰی یَوۡمِ الۡوَقۡتِ الۡمَعۡلُوۡمِ ﴿۸۱﴾  ",
    "ayat_id": 4088,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "اس نے کہا مجھے تیری عزت کی قسم! میں ان سب کو ضرور گمراہ کروں گا۔",
    "arabic": "قَالَ فَبِعِزَّتِکَ لَاُغۡوِیَنَّہُمۡ اَجۡمَعِیۡنَ ﴿ۙ۸۲﴾ ",
    "ayat_id": 4089,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "ان میں سے تیرے مخلص (خاص) بندوں کے سوا۔",
    "arabic": "اِلَّا عِبَادَکَ مِنۡہُمُ  الۡمُخۡلَصِیۡنَ ﴿۸۳﴾ ",
    "ayat_id": 4090,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "(اللہ نے) فرمایا یہ سچ ہے اور میں سچ ہی کہتا ہوں۔",
    "arabic": "قَالَ  فَالۡحَقُّ ۫ وَ الۡحَقَّ  اَقُوۡلُ ﴿ۚ۸۴﴾ ",
    "ayat_id": 4091,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
  {
    "urdu": "میں ضرور جہنم بھر دوں گا تجھ سے اور اُن سب سے جو تیرے پیچھے چلیں۔",
    "arabic":
        "لَاَمۡلَئَنَّ جَہَنَّمَ مِنۡکَ وَ مِمَّنۡ تَبِعَکَ مِنۡہُمۡ  اَجۡمَعِیۡنَ  ﴿۸۵﴾ ",
    "ayat_id": 4092,
    "surah_id": 38,
    "surah_name": "Ṣād",
    "para_id": 23,
    "para_name": "Wa Mali"
  },
