  },
  {
    "urdu": "اور چھت بلند کی، ",
    "arabic": "وَ  السَّقۡفِ الۡمَرۡفُوۡعِ ۙ﴿۵﴾ ",
    "ayat_id": 4791,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "اور جوش مارتے دریا کی،",
    "arabic": "وَ الۡبَحۡرِ  الۡمَسۡجُوۡرِ ۙ﴿۶﴾ ",
    "ayat_id": 4792,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "بے شک تیرے رب کا عذاب ضرور واقع ہونے والا ہے۔",
    "arabic": "اِنَّ عَذَابَ رَبِّکَ  لَوَاقِعٌ ۙ﴿۷﴾ ",
    "ayat_id": 4793,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "اسے کوئی ٹالنے والا نہیں۔",
    "arabic": "مَّا لَہٗ  مِنۡ  دَافِعٍ ۙ﴿۸﴾ ",
    "ayat_id": 4794,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "جس دن تھرتھرائے گا آسمان (بُری طرح) تھرتھرا کر،",
    "arabic": "یَّوۡمَ تَمُوۡرُ السَّمَآءُ  مَوۡرًا ۙ﴿۹﴾ ",
    "ayat_id": 4795,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "اور پہاڑ چلنے کی طرح چلیں گے (اُڑے پھریں گے)۔",
    "arabic": "وَّ تَسِیۡرُ الۡجِبَالُ سَیۡرًا ﴿ؕ۱۰﴾ ",
    "ayat_id": 4796,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "سو اس دن جھٹلانے والوں کے لیے بربادی ہے۔",
    "arabic": "فَوَیۡلٌ  یَّوۡمَئِذٍ  لِّلۡمُکَذِّبِیۡنَ ﴿ۙ۱۱﴾ ",
    "ayat_id": 4797,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "وہ جو مشغلے میں (بیہودگی سے) کھیلتے ہیں۔",
    "arabic": "الَّذِیۡنَ ہُمۡ فِیۡ خَوۡضٍ یَّلۡعَبُوۡنَ ﴿ۘ۱۲﴾ ",
    "ayat_id": 4798,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "جس دن وہ جہنم کی آگ کی طرف دھکے دے کر دھکیلے جائیں گے۔",
    "arabic": "یَوۡمَ  یُدَعُّوۡنَ  اِلٰی نَارِ  جَہَنَّمَ دَعًّا ﴿ؕ۱۳﴾ ",
    "ayat_id": 4799,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "یہ ہے وہ آگ جس کو تم جھٹلاتے تھے،",
    "arabic": "ہٰذِہِ  النَّارُ الَّتِیۡ  کُنۡتُمۡ  بِہَا تُکَذِّبُوۡنَ ﴿۱۴﴾ ",
    "ayat_id": 4800,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "تو کیا یہ جادو ہے؟ یا تم کو دکھائی نہیں دیتا؟ ",
    "arabic": "اَفَسِحۡرٌ  ہٰذَاۤ   اَمۡ  اَنۡتُمۡ لَا تُبۡصِرُوۡنَ ﴿ۚ۱۵﴾ ",
    "ayat_id": 4801,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "تم اس میں داخل ہوجاؤ، پھر تم صبر کرو، یا نہ صبر کرو، تمہارے لیے برابر ہے، اس کے سوا نہیں جو تم کرتے تھےتمہیں (اس کا) بدلہ دیا جائے گا۔",
    "arabic":
        "اِصۡلَوۡہَا فَاصۡبِرُوۡۤا  اَوۡ لَا  تَصۡبِرُوۡا ۚ سَوَآءٌ  عَلَیۡکُمۡ ؕ اِنَّمَا تُجۡزَوۡنَ مَا کُنۡتُمۡ تَعۡمَلُوۡنَ ﴿۱۶﴾ ",
    "ayat_id": 4802,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "بے شک متقی (بہشت کے) باغوں اور نعمتوں میں ہوں گے۔",
    "arabic": "اِنَّ  الۡمُتَّقِیۡنَ فِیۡ جَنّٰتٍ وَّ نَعِیۡمٍ ﴿ۙ۱۷﴾ ",
    "ayat_id": 4803,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "اس کے ساتھ خوش ہوں گے جو ان کے رب نے انہیں دیا، اور ان کے رب نے انہیں دوزخ کے عذاب سے بچا لیا۔",
    "arabic":
        "فٰکِہِیۡنَ بِمَاۤ  اٰتٰہُمۡ  رَبُّہُمۡ ۚ وَ  وَقٰہُمۡ  رَبُّہُمۡ  عَذَابَ  الۡجَحِیۡمِ ﴿۱۸﴾ ",
    "ayat_id": 4804,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "تم کھاؤ اور پیئو رچتے پچتے (جی بھر کر) اس کے بدلے میں جو تم کرتے تھے۔",
    "arabic":
        "کُلُوۡا وَ اشۡرَبُوۡا ہَنِیۡٓئًۢا بِمَا کُنۡتُمۡ تَعۡمَلُوۡنَ ﴿ۙ۱۹﴾ ",
    "ayat_id": 4805,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "تختوں پر صف بستہ تکیہ لگائے ہوئے۔ اور ہم نے ان کی زوجیت میں دیا بڑی آنکھوں والی حُوروں کو۔",
    "arabic":
        "مُتَّکِئِیۡنَ عَلٰی سُرُرٍ  مَّصۡفُوۡفَۃٍ ۚ وَ زَوَّجۡنٰہُمۡ  بِحُوۡرٍ عِیۡنٍ ﴿۲۰﴾ ",
    "ayat_id": 4806,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "اور جو لوگ ایمان لائے اور ان کی اولاد نے ایمان کے ساتھ ان کی پیروی کی، ہم نے ان کی اولاد کو ان کے ساتھ ملا دیا، اور ہم نے ان کے عمل سے کچھ کمی نہیں کی، ہر آدمی اپنے اعمال میں رہن ہے۔",
    "arabic":
        "وَ الَّذِیۡنَ اٰمَنُوۡا وَ اتَّبَعَتۡہُمۡ  ذُرِّیَّتُہُمۡ بِاِیۡمَانٍ  اَلۡحَقۡنَا بِہِمۡ  ذُرِّیَّتَہُمۡ  وَ مَاۤ  اَلَتۡنٰہُمۡ  مِّنۡ عَمَلِہِمۡ  مِّنۡ شَیۡءٍ ؕ کُلُّ امۡرِیًٔۢ  بِمَا کَسَبَ  رَہِیۡنٌ ﴿۲۱﴾ ",
    "ayat_id": 4807,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "اور ہم پھلوں کے ساتھ ان کی مدد کریں گے اور گوشت (سے) جو ان کا جی چاہے گا۔",
    "arabic":
        "وَ اَمۡدَدۡنٰہُمۡ  بِفَاکِہَۃٍ  وَّ لَحۡمٍ  مِّمَّا یَشۡتَہُوۡنَ ﴿۲۲﴾ ",
    "ayat_id": 4808,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "وہ اس میں اس پیالے کی چھینا جھپٹی کریں گے جس میں نہ بکواس ہوگی نہ گناہ کی بات۔",
    "arabic":
        "یَتَنَازَعُوۡنَ  فِیۡہَا کَاۡسًا  لَّا  لَغۡوٌ  فِیۡہَا وَ لَا  تَاۡثِیۡمٌ ﴿۲۳﴾ ",
    "ayat_id": 4809,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "اور ان کے اِردگرد پھریں گے خدمت گار لڑکے۔ گویا وہ چھپا کر رکھے ہوئے موتی ہیں۔",
    "arabic":
        "وَ یَطُوۡفُ عَلَیۡہِمۡ غِلۡمَانٌ  لَّہُمۡ  کَاَنَّہُمۡ لُؤۡلُؤٌ  مَّکۡنُوۡنٌ ﴿۲۴﴾ ",
    "ayat_id": 4810,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "اور ان میں سے ایک دوسرے کی طرف متوجہ ہوگا آپس میں پوچھتے ہوئے۔",
    "arabic": "وَ اَقۡبَلَ بَعۡضُہُمۡ عَلٰی بَعۡضٍ یَّتَسَآءَلُوۡنَ ﴿۲۵﴾ ",
    "ayat_id": 4811,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "وہ کہیں گے بے شک ہم اس سے پہلے اپنے اہلِ خانہ میں ڈرتے تھے۔",
    "arabic":
        "قَالُوۡۤا اِنَّا کُنَّا قَبۡلُ فِیۡۤ   اَہۡلِنَا مُشۡفِقِیۡنَ ﴿۲۶﴾ ",
    "ayat_id": 4812,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "تو اللہ نے ہم پر احسان کیا اور ہمیں بچا لیا لُو کے عذاب سے۔",
    "arabic":
        "فَمَنَّ اللّٰہُ عَلَیۡنَا وَ  وَقٰىنَا عَذَابَ السَّمُوۡمِ ﴿۲۷﴾ ",
    "ayat_id": 4813,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "بے شک اس سے قبل ہم اس کو پکارتے تھے، بے شک وہی احسان کرنے والا، رحم کرنے والا ہے۔",
    "arabic":
        "اِنَّا کُنَّا مِنۡ قَبۡلُ نَدۡعُوۡہُ ؕ اِنَّہٗ  ہُوَ الۡبَرُّ   الرَّحِیۡمُ ﴿٪۲۸﴾ ",
    "ayat_id": 4814,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "پس آپ (ﷺ) نصیحت کرتے رہیں، پس (ﷺ) اپنے رب کے فضل سے نہ کاہن ہیں، نہ دیوانے۔",
    "arabic":
        "فَذَکِّرۡ  فَمَاۤ   اَنۡتَ بِنِعۡمَتِ رَبِّکَ بِکَاہِنٍ  وَّ لَا  مَجۡنُوۡنٍ ﴿ؕ۲۹﴾ ",
    "ayat_id": 4815,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "کیا وہ کہتے ہیں یہ شاعر ہے، ہم اس کے ساتھ حوادثِ زمانہ کے منتظر ہیں۔",
    "arabic":
        "اَمۡ  یَقُوۡلُوۡنَ  شَاعِرٌ  نَّتَرَبَّصُ بِہٖ  رَیۡبَ الۡمَنُوۡنِ ﴿۳۰﴾ ",
    "ayat_id": 4816,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "آپ (ﷺ) فرمادیں تم انتظار کرو، بے شک میں (بھی) تمہارے ساتھ انتظار کرنے والوں میں سے ہوں۔",
    "arabic":
        "قُلۡ تَرَبَّصُوۡا فَاِنِّیۡ مَعَکُمۡ  مِّنَ الۡمُتَرَبِّصِیۡنَ ﴿ؕ۳۱﴾ ",
    "ayat_id": 4817,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "کیا ان کی عقلیں انہیں یہی سکھاتی ہیں؟ یا وہ سرکش لوگ ہیں۔",
    "arabic":
        "اَمۡ  تَاۡمُرُہُمۡ  اَحۡلَامُہُمۡ  بِہٰذَاۤ   اَمۡ  ہُمۡ قَوۡمٌ  طَاغُوۡنَ ﴿ۚ۳۲﴾ ",
    "ayat_id": 4818,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "کیا وہ کہتے ہیں کہ اس نے اسے (قرآن) کو گھڑ لیا ہے (نہیں) بلکہ وہ ایمان نہیں لاتے۔",
    "arabic":
        "اَمۡ  یَقُوۡلُوۡنَ تَقَوَّلَہٗ ۚ بَلۡ  لَّا  یُؤۡمِنُوۡنَ ﴿ۚ۳۳﴾ ",
    "ayat_id": 4819,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "تو چاہیے کہ وہ اس جیسی ایک بات لے آئیں اگر وہ سچے ہیں۔",
    "arabic":
        "فَلۡیَاۡتُوۡا بِحَدِیۡثٍ مِّثۡلِہٖۤ   اِنۡ  کَانُوۡا صٰدِقِیۡنَ ﴿ؕ۳۴﴾ ",
    "ayat_id": 4820,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "کیا وہ پیدا کیے گئے ہیں بغیر کسی شے (بنانے والے) کے، یا وہ (خود) پیدا کرنے والے ہیں۔",
    "arabic":
        "اَمۡ  خُلِقُوۡا  مِنۡ غَیۡرِ  شَیۡءٍ  اَمۡ ہُمُ الۡخٰلِقُوۡنَ ﴿ؕ۳۵﴾ ",
    "ayat_id": 4821,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu":
        "کیا انہوں نے پیدا کیا آسمانوں کو اور زمین کو؟ (نہیں) بلکہ وہ یقین نہیں رکھتے۔",
    "arabic":
        "اَمۡ خَلَقُوا السَّمٰوٰتِ  وَ الۡاَرۡضَ ۚ بَلۡ  لَّا یُوۡقِنُوۡنَ ﴿ؕ۳۶﴾ ",
    "ayat_id": 4822,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
    "urdu": "کیا ان کے پاس تیرے رب (کی رحمت) کے خزانے ہیں؟ یا وہ داروغ ہیں؟",
    "arabic":
        "اَمۡ  عِنۡدَہُمۡ  خَزَآئِنُ رَبِّکَ  اَمۡ  ہُمُ الۡمُصَۜیۡطِرُوۡنَ ﴿ؕ۳۷﴾ ",
    "ayat_id": 4823,
    "surah_id": 52,
    "surah_name": "At-Tur",
    "para_id": 27,
    "para_name": "Qala Fama Khatbukum"
  },
  {
