    "arabic": "اَیَحۡسَبُ الۡاِنۡسَانُ اَلَّنۡ نَّجۡمَعَ عِظَامَہٗ ؕ﴿۳﴾ ",
    "ayat_id": 5628,
    "surah_id": 75,
    "surah_name": "Al-Qiyama",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "کیوں نہیں؟ کہ ہم اس پر قادرہیں کہ اس کے پورے پورے درست کردیں۔",
    "arabic": "بَلٰی قٰدِرِیۡنَ  عَلٰۤی  اَنۡ  نُّسَوِّیَ بَنَانَہٗ ﴿۴﴾ ",
    "ayat_id": 5629,
    "surah_id": 75,
    "surah_name": "Al-Qiyama",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "بلکہ انسان چاہتا یہ ہے کہ آگے کو بھی گناہ کرتا رہے۔",
    "arabic": "بَلۡ یُرِیۡدُ الۡاِنۡسَانُ  لِیَفۡجُرَ  اَمَامَہٗ ۚ﴿۵﴾ ",
    "ayat_id": 5630,
    "surah_id": 75,
    "surah_name": "Al-Qiyama",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
