    "ayat_id": 5631,
    "surah_id": 75,
    "surah_name": "Al-Qiyama",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "پس جب آنکھیں چندھیا جائیں گی",
