    "ayat_id": 5632,
    "surah_id": 75,
    "surah_name": "Al-Qiyama",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اور چاند کو گرہن لگ جائے گا،",
    "arabic": "وَ  خَسَفَ الۡقَمَرُ ۙ﴿۸﴾ ",
    "ayat_id": 5633,
    "surah_id": 75,
    "surah_name": "Al-Qiyama",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اور سوج اور چاند جمع کردئیے جائیں گے",
    "arabic": "وَ  جُمِعَ الشَّمۡسُ وَ الۡقَمَرُ ۙ﴿۹﴾ ",
    "ayat_id": 5634,
    "surah_id": 75,
    "surah_name": "Al-Qiyama",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
