    "arabic":
        "یُنَبَّؤُا الۡاِنۡسَانُ یَوۡمَئِذٍۭ بِمَا قَدَّمَ وَ اَخَّرَ ﴿ؕ۱۳﴾ ",
    "ayat_id": 5638,
    "surah_id": 75,
    "surah_name": "Al-Qiyama",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "بلکہ انسان اپنی جان پر باخبر ہے۔",
    "arabic": "بَلِ الۡاِنۡسَانُ  عَلٰی نَفۡسِہٖ بَصِیۡرَۃٌ ﴿ۙ۱۴﴾ ",
    "ayat_id": 5639,
    "surah_id": 75,
    "surah_name": "Al-Qiyama",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
