    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "بادلوں کو اُٹھا کر لانے والی، پھیلانے والی ہواؤں کی قسم،",
    "arabic": "وَّ  النّٰشِرٰتِ نَشۡرًا ۙ﴿۳﴾ ",
    "ayat_id": 5701,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "پھر بانٹ کر پھاڑنے والی ہواؤں کی قسم،",
    "arabic": "فَالۡفٰرِقٰتِ فَرۡقًا ۙ﴿۴﴾ ",
    "ayat_id": 5702,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "پھر وحی القا کرنے والے فرشتوں کی قسم",
    "arabic": "فَالۡمُلۡقِیٰتِ ذِکۡرًا ۙ﴿۵﴾ ",
    "ayat_id": 5703,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "حجت تمام کرنے کو یا ڈرانے کو۔",
    "arabic": "عُذۡرًا  اَوۡ  نُذۡرًا ۙ﴿۶﴾ ",
    "ayat_id": 5704,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "بے شک جو تمہیں وعدہ دیا جاتا ہے وہ ضرور واقع ہونے والا ہے۔",
    "arabic": "اِنَّمَا تُوۡعَدُوۡنَ  لَوَاقِعٌ ؕ﴿۷﴾ ",
    "ayat_id": 5705,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "پھر جب ستارے بے نور ہوجائیں۔",
    "arabic": "فَاِذَا النُّجُوۡمُ  طُمِسَتۡ ۙ﴿۸﴾ ",
    "ayat_id": 5706,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اور جب آسمان پھٹ جائے۔",
    "arabic": "وَ  اِذَا السَّمَآءُ  فُرِجَتۡ ۙ﴿۹﴾ ",
    "ayat_id": 5707,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اور جب پہاڑ اُڑتے (پارہ پارہ ہوکر) پھریں۔",
    "arabic": "وَ  اِذَا  الۡجِبَالُ  نُسِفَتۡ ﴿ۙ۱۰﴾ ",
    "ayat_id": 5708,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اور جب رسولِ(ﷺ)  وقت (معین) پر جمع کیے جائیں،",
    "arabic": "وَ  اِذَا  الرُّسُلُ  اُقِّتَتۡ ﴿ؕ۱۱﴾ ",
    "ayat_id": 5709,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "(ان کا معاملہ) کس دن کے لیے ملتوی رکھا گیا ہے؟",
    "arabic": "لِاَیِّ  یَوۡمٍ اُجِّلَتۡ ﴿ؕ۱۲﴾ ",
    "ayat_id": 5710,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "فیصلے کے دن کے لیے",
    "arabic": "لِیَوۡمِ  الۡفَصۡلِ ﴿ۚ۱۳﴾ ",
    "ayat_id": 5711,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اور تم کیا سمجھے فیصلے کا دن کیا ہے؟",
    "arabic": "وَ  مَاۤ   اَدۡرٰىکَ مَا یَوۡمُ الۡفَصۡلِ ﴿ؕ۱۴﴾ ",
    "ayat_id": 5712,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اس دن خرابی ہے جھٹلانے والوں کے لیے،",
    "arabic": "وَیۡلٌ  یَّوۡمَئِذٍ  لِّلۡمُکَذِّبِیۡنَ ﴿۱۵﴾ ",
    "ayat_id": 5713,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "کیا ہم نے ہلاک نہیں کیا پہلے لوگوں کو؟",
    "arabic": "اَلَمۡ  نُہۡلِکِ  الۡاَوَّلِیۡنَ ﴿ؕ۱۶﴾ ",
    "ayat_id": 5714,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "پھر پچھلوں کو ان کے پیچھے چلاتے ہیں۔",
    "arabic": "ثُمَّ  نُتۡبِعُہُمُ   الۡاٰخِرِیۡنَ ﴿۱۷﴾ ",
    "ayat_id": 5715,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اسی طرح ہم مُجرموں کے ساتھ کرتے ہیں۔",
    "arabic": "کَذٰلِکَ نَفۡعَلُ  بِالۡمُجۡرِمِیۡنَ ﴿۱۸﴾ ",
    "ayat_id": 5716,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اس دن خرابی ہے جھٹلانے والوں کے لیے۔",
    "arabic": "وَیۡلٌ  یَّوۡمَئِذٍ  لِّلۡمُکَذِّبِیۡنَ ﴿۱۹﴾ ",
    "ayat_id": 5717,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "کیا ہم نے تمہیں حقیر پانی سے نہیں پیدا کیا؟",
    "arabic": "اَلَمۡ  نَخۡلُقۡکُّمۡ  مِّنۡ مَّآءٍ مَّہِیۡنٍ ﴿ۙ۲۰﴾ ",
    "ayat_id": 5718,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "پھر ہم نے اسے ایک محفوظ جگہ میں رکھا،",
    "arabic": "فَجَعَلۡنٰہُ  فِیۡ  قَرَارٍ  مَّکِیۡنٍ ﴿ۙ۲۱﴾ ",
    "ayat_id": 5719,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "ایک وقتِ معین تک،",
    "arabic": "اِلٰی  قَدَرٍ  مَّعۡلُوۡمٍ ﴿ۙ۲۲﴾ ",
    "ayat_id": 5720,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "پھر ہم نے اندازہ کیا تو (ہم) کیسا اچھا اندازہ کرنے والے ہیں!",
    "arabic": "فَقَدَرۡنَا ٭ۖ فَنِعۡمَ  الۡقٰدِرُوۡنَ ﴿۲۳﴾ ",
    "ayat_id": 5721,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اِس دن خرابی ہے جھٹلانے والوں کے لیے۔",
    "arabic": "وَیۡلٌ  یَّوۡمَئِذٍ  لِّلۡمُکَذِّبِیۡنَ ﴿۲۴﴾ ",
    "ayat_id": 5722,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "کیا ہم نے زمین کو سمیٹنے والی نہیں بنایا؟",
    "arabic": "اَلَمۡ  نَجۡعَلِ الۡاَرۡضَ  کِفَاتًا ﴿ۙ۲۵﴾ ",
    "ayat_id": 5723,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "زندوں کو اور مُردوں کو۔",
    "arabic": "اَحۡیَآءً   وَّ  اَمۡوَاتًا ﴿ۙ۲۶﴾ ",
    "ayat_id": 5724,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu":
        "اور ہم نے اس میں اونچے اونچے پہاڑ رکھے، اور ہم نے تمہیں میٹھا پانی پلایا۔",
    "arabic":
        "وَّ جَعَلۡنَا فِیۡہَا رَوَاسِیَ شٰمِخٰتٍ وَّ اَسۡقَیۡنٰکُمۡ مَّآءً  فُرَاتًا ﴿ؕ۲۷﴾ ",
    "ayat_id": 5725,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "خرابی ہے اس دن جھٹلانے والوں کے لیے۔",
    "arabic": "وَیۡلٌ  یَّوۡمَئِذٍ  لِّلۡمُکَذِّبِیۡنَ ﴿۲۸﴾ ",
    "ayat_id": 5726,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "(حکم ہوگا) تم چلو اس کی طرف جس کو تم جھٹلاتے تھے۔",
    "arabic": "اِنۡطَلِقُوۡۤا  اِلٰی مَا کُنۡتُمۡ بِہٖ تُکَذِّبُوۡنَ ﴿ۚ۲۹﴾ ",
    "ayat_id": 5727,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "تم چلو تین شاخوں والے سایہ کی طرف۔",
    "arabic": "اِنۡطَلِقُوۡۤا  اِلٰی ظِلٍّ  ذِیۡ ثَلٰثِ شُعَبٍ ﴿ۙ۳۰﴾ ",
    "ayat_id": 5728,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "نہ گہرا سایہ، اور نہ وہ تپش سے بچائے۔",
    "arabic": "لَّا  ظَلِیۡلٍ  وَّ لَا یُغۡنِیۡ مِنَ اللَّہَبِ ﴿ؕ۳۱﴾ ",
    "ayat_id": 5729,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "بے شک وہ محل جیسے (اونچے) شعلے پھینکتی ہے،",
    "arabic": "اِنَّہَا  تَرۡمِیۡ  بِشَرَرٍ  کَالۡقَصۡرِ ﴿ۚ۳۲﴾ ",
    "ayat_id": 5730,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "گویاکہ وہ اونٹ ہیں زرد۔",
    "arabic": "کَاَنَّہٗ  جِمٰلَتٌ صُفۡرٌ ﴿ؕ۳۳﴾ ",
    "ayat_id": 5731,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "خرابی ہے اِس دن جھٹلانے والوں کے لیے۔",
    "arabic": "وَیۡلٌ  یَّوۡمَئِذٍ لِّلۡمُکَذِّبِیۡنَ ﴿۳۴﴾ ",
    "ayat_id": 5732,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اِس دن نہ وہ بول سکیں گے۔",
    "arabic": "ہٰذَا یَوۡمُ لَا  یَنۡطِقُوۡنَ ﴿ۙ۳۵﴾ ",
    "ayat_id": 5733,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اور نہ انہیں اجازت دی جائے گی، کہ وہ عذر خواہی کریں۔",
    "arabic": "وَ لَا یُؤۡذَنُ  لَہُمۡ  فَیَعۡتَذِرُوۡنَ ﴿۳۶﴾ ",
    "ayat_id": 5734,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "خرابی ہے اس دن جُھٹلانے والوں کے لیے۔",
    "arabic": "وَیۡلٌ  یَّوۡمَئِذٍ  لِّلۡمُکَذِّبِیۡنَ ﴿۳۷﴾ ",
    "ayat_id": 5735,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "یہ فیصلے کا دن ہے، ہم نے تمہیں جمع کیا اور پہلے لوگوں کو۔",
    "arabic": "ہٰذَا یَوۡمُ الۡفَصۡلِ ۚ جَمَعۡنٰکُمۡ وَ الۡاَوَّلِیۡنَ ﴿۳۸﴾ ",
    "ayat_id": 5736,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "پھر اگر تمہارے پاس کوئی داؤ ہے تو مجھ پر داؤ کرلو۔",
    "arabic": "فَاِنۡ کَانَ  لَکُمۡ  کَیۡدٌ  فَکِیۡدُوۡنِ ﴿۳۹﴾ ",
    "ayat_id": 5737,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "خرابی ہے اس دن جُھٹلانے والوں کے لیے۔",
    "arabic": "وَیۡلٌ  یَّوۡمَئِذٍ لِّلۡمُکَذِّبِیۡنَ ﴿٪۴۰﴾ ",
    "ayat_id": 5738,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "بے شک پرہیزگار سایوں اور چشوں میں ہوں گے۔",
    "arabic": "اِنَّ  الۡمُتَّقِیۡنَ فِیۡ ظِلٰلٍ وَّ  عُیُوۡنٍ ﴿ۙ۴۱﴾  ",
    "ayat_id": 5739,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "اور اس قسم کے میووں میں جو وہ چاہیں گے۔",
    "arabic": "وَّ  فَوَاکِہَ  مِمَّا یَشۡتَہُوۡنَ ﴿ؕ۴۲﴾ ",
    "ayat_id": 5740,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu":
        "(ہم فرمائیں گے) تم کھاؤ اور پیئو مزے سے (بافراغت) اس کے بدلے جو تم کرتے تھے۔",
    "arabic":
        "کُلُوۡا  وَ اشۡرَبُوۡا ہَنِیۡٓئًۢا بِمَا کُنۡتُمۡ تَعۡمَلُوۡنَ ﴿۴۳﴾ ",
    "ayat_id": 5741,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "بے شک ہم اسی طرح نیکوکاروں کو جزا دیتے ہیں۔",
    "arabic": "اِنَّا کَذٰلِکَ  نَجۡزِی  الۡمُحۡسِنِیۡنَ ﴿۴۴﴾ ",
    "ayat_id": 5742,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
    "para_id": 29,
    "para_name": "Tabarakallazi"
  },
  {
    "urdu": "خرابی ہے اس دن جھٹلانے والوں کے لیے۔",
    "arabic": "وَیۡلٌ  یَّوۡمَئِذٍ لِّلۡمُکَذِّبِیۡنَ ﴿۴۵﴾ ",
    "ayat_id": 5743,
    "surah_id": 77,
    "surah_name": "Al-Mursalat",
